#include <bits/stdc++.h>
using namespace std;

int main(){
    int a = 10,b = 2;
     swap(a,b);
     cout << a <<" " << b << endl;
     
     cout <<"Max :" <<max(a,b) << endl;
     cout <<"Min :" <<min(a,b) << endl;

     int c = 25;
     
     cout << sqrt(c) << endl;

     cout << pow(a,b) << endl;

     float d = 12.26;
     cout << ceil(d) << endl;

     float e = 12.15;
     cout << floor(e) << endl;

     float f = 12.58;
     cout << round(f) << endl;

    return 0;
}